mapper.update:
UPDATE board
SET title=#{title}, content=#{content}
WHERE board_idx = #{board_idx}

update board
set viewcnt = viewcnt + 1
where board_idx = #{board_idx}

mapper.select:
select * 
	from heart 
	where board_idx = #{board_idx} and member_idx = #{member_idx}

mapper.delete:
DELETE
	FROM heart
	WHERE board_idx = #{board_idx} and member_idx = #{member_idx}

mapper.update:
update board 
	set heartcnt = heartcnt + 1 
	where board_idx = #{board_idx};

update board 
		set heartcnt = heartcnt - 1 
		where board_idx = #{board_idx};
